import React from 'react';
import './styles/Card.css'; // Optional: for styling

function Card({ card, onClick, isFlipped }) {
  return (
    <div className="card" onClick={onClick}>
      {isFlipped ? (
        <div className="card-content">{card}</div>
      ) : (
        <div className="card-back"></div>
      )}
    </div>
  );
}

export default Card;
